er by topico asc');        $total = mysql_num_rows($sql);    	if($total > 0){				while($dados = mysql_fetch_assoc($sql)){			echo '<a href="javascript: void(0)" rel="'.$dados['topico'].'" class="topico">'.$dados['topico'].'</a>';		}		?>				<script type="text/javascript">            visible = true;			$('.topico').hover(function(){                $('#sugestao a').removeClass('cele')                $(this).addClass('cele');            }).click(function(){				var termo = $('#pesquisa').val().split(',');				termo.pop();								termo = termo.toString();								$('#pesquisa').val((termo == '' ? termo : termo+', ')+$(this).attr('rel')+', ').focus();				$('#sugestao').fadeOut(500);								return false;			});		</script>		<?php    }break;}?>